//axios perlu download terlebih dahulu melalui cmd
import axios, { Axios } from "axios";

const api_key = process.env.REACT_APP_BASEURL

//memanggil data movie dari api
//async karena api dari luar
//export agar bisa dilempar keluar
export const getMovieList = async() => {
    const movie = await axios.get('${process.env.REACT_APP_BASEURL}/movie/popular?api_key${process.env.REACT_APP_APIKEY}')
    console.log({movieList: movie})
}


export const searchMovie = async (q) => {
    const search = await axios.get(q)
    return
}
