import './App.css';

//cara import API yang telah dibuat di (api.js) atau yang telah diexport
import {getMovieList, searchMovie} from "./api"
import { useEffect, useState } from 'react';

const App = () => {

  const [popularMovies, setPopularMovies] = useState([])

  //agar running pertamakali saat mau ambil data axios
  useEffect (() => {
    getMovieList().then((result) => {
      setPopularMovies(result)
    }).then (()=>{
      console.log({popularMovies: popularMovies})
    })
  }, [])



  const search = (q) => {
    console.log({q})
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Test</h1>
        <input 
          placeholder='Cari Film Kesayangan'  
          className='movie-search'
          onChange={({target}) => search(target.value)}
        />
        <div className='movie-container'>
          <div className='movie-wrapper'>
            <div className='movie-title'>Movie Pertama</div>
            <img className='movie-image'></img>
            <div className='movie-date'>11-12-2022</div>
            <div className='movie-rate'>8.09</div>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
